func MergeSorted(l, f *List) (m *List, ok bool) {
	m = new(List)
	for l.Len() > 0 || f.Len() > 0 {
		vl, nl, okl := PopInt(l)
		vf, nf, okf := PopInt(f)
		if !okl || !okf {
			return m, false
		}

		ll, n := l, nl // The assumption is: vl <= vf.
		switch {
		case l.Len() == 0:
			ll, n = f, nf
		case f.Len() == 0:
			ll, n = l, nl
		case vl > vf:
			ll, n = f, nf
		}

		m.Insert(ll.Remove(n))
	}
	return m, true
}
```

